INSERT INTO `principles` (`id`, `first_method`, `first_method_heading`, `second_rule`, `second_rule_heading`, `third_meditation`, `third_meditation_heading`, `created_at`, `updated_at`) VALUES
(1, '<ul>\n	<li><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Basal dose of krushi amrut , root charger and nutri charger should be given after every 60 days as per requirement of the plant .&nbsp; </span></span></li>\n	<li><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">In every application of soil or drip or drenching at least once in a week use soil charger 1 ltr and health charger 600 gm for 1 acre&nbsp;&nbsp; . </span></span></li>\n	<li><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Each spray must be mixed with a fruit charger.&nbsp;</span></span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</li>\n</ul>', '<p style=\"text-align:center\"><strong><em>Important</em></strong><br />\n<strong><u>Understand the meaning of 100% SCT ...</u></strong></p>', '<ul>\n	<li><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Do not cultivate any soil that will cause movement or exposure . Mulching should not be done by part of trunk.( weeds should not be cut in rainy weather or when it is raining , do in a dry environment )</span></span></li>\n	<li><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Do not use any chemical fertilizer ( granular or water soluble ) . </span></span></li>\n	<li><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">For crop protection use only pest fighter , pest cleaner , disease fighter and fungi cleaner.</span></span>&nbsp;<span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Do not use any chemicals .&nbsp;</span></span></li>\n</ul>', '<p style=\"text-align:center\"><strong><em>More Important</em></strong><br />\n<strong><u>Things that are stickly prohibited to do. It must be avoided.</u></strong></p>', '<ul>\n	<li><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Watching daily videos posted on youtube channel and prepare notes and comments.</span></span><br />\n	<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Times New Roman&quot;,serif\">This is the breath of SCT VEDIC. </span></strong></span></span></li>\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Study the articles published daily on WhatsApp group.</span></span></span><br />\n	<strong><span style=\"font-family:&quot;Times New Roman&quot;,serif\">This is the water for SCT VEDIC users.</span></strong></li>\n	<li><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">3 -5 min of daily discussion with at least one new or old user farmer or with each other directly or on call about SCT issue.</span></span><br />\n	<span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-family:&quot;Times New Roman&quot;,serif\">This is the food for SCT USER</span></strong></span></li>\n</ul>', '<p style=\"text-align:center\"><strong><em>Most&nbsp;Important</em></strong></p>', '2021-11-20 15:03:07', '2023-01-30 10:18:40');
