INSERT INTO `tbl_web_blog` (`id`, `title`, `content`, `photo_one`, `photo_two`, `language`, `created_by`, `articleorschedule`, `status`, `created_at`, `updated_at`) VALUES
(63, 'बहार धरलेल्या बागेत तणनाशकांचा वापर म्हणजे माणसाला जिवंत जाळणे होय ‼', '<p><strong>&nbsp;आजचा मंत्र&nbsp;</strong></p>\n\n<p><strong>&nbsp;SCT - वैदिक तंत्रज्ञान&nbsp;</strong><br />\n<strong>बहार धरलेल्या बागेत तणनाशकांचा वापर म्हणजे...<br />\n&nbsp; &nbsp;माणसाला जिवंत जाळणे होय ‼</strong></p>\n\n<p>&nbsp;मेलेल्या प्रेताला जाळणे व माणसाला जिवंत जाळणे यात जेवढा फरक आहे तेवढा फरक बागेत पांढरी मुळे सक्रीय असताना &quot;तणनाशक&quot; मारल्यामुळे मातिवर , जीवाणुंवर पर्यायाने पिकावर पडतो ‼<br />\n&nbsp; &nbsp; &nbsp; त्याचे भयंकर दुष्परिणाम होतात !! स्वता तननाशक बनावनाऱ्या कंपनीच्या मालकांनी हे मान्य केले आहे!! तननाशकचे घटक श्वास व अन्नातुन पोटात गेल्यास कैन्सर होतो हे सिद्ध झाल्यामुळे अमेरिकेत यावर बंदी आहे‼<br />\n&nbsp; &nbsp; त्याचे परिणाम दूरगामी मातीच्या सुपिक्तेवर होतात व कमी झालेली सुपिकता भरुन काढण्यासाठी जीवामृत स्लरी/, जिवाणु खते असे अनेक उपाय करावे लागतात !! पुढे जावून खुप कष्ट व पैसा खर्च होतो ‼<br />\n&nbsp; &nbsp; &nbsp; काही कालावधी नंतर &nbsp;झाडाच्या आरोग्यावर दुष्परिणाम होतात !! त्यामुळे मर , तेल्या , डाग असे प्रॉब्लम वाढतात ‼<br />\n&nbsp; &nbsp; &nbsp; &nbsp;जे शेतकरी केवळ तणनाशकांचा याचा नियमित वापर करतात ते स्वताच्या कुटुंबाच्या व मातीच्या भविष्या सोबत घातक जीवघेणा खेळ खेळत असतात!! पिकात कमवलेले पैसा दवाखान्यात वाया घालवत असतात‼<br />\n&nbsp; &nbsp; &nbsp;कांदा व इतर भाजीपाला पिकामध्ये तर तणनाशकसोबत सॉइल चार्जर वापरने अत्यंत आवश्यक आहे‼ कारण ही कमी कालावधीत आपल्या आहारात येणार असतात‼<br />\n&nbsp; कारण सॉइल चार्जर टेक्नोलॉजी मध्ये 15% नैचरल फुल्विक एसिड आहे जे एवढ्या मोठ्या प्रमाणात नैसर्गिकपने कोठेही उपलब्ध होत नाही‼<br />\n&nbsp; &nbsp;परिणामि सॉइल चार्जर तणनाशकाचा मातीतिल जिवाणूंवर दुष्परिणाम होवू देत नाही !! हे तणनाशक व जिवाणु यामध्ये सुरक्षित शील्ड तयार करते‼<br />\n&nbsp; &nbsp; तसेच दोन्ही चार्जर अतिशय प्रभावी एक्टिवेटर आहेत परिणामी तण नाशकांचे अत्यंत प्रभावी शोषण घडवून आणते!! त्यामुळे कमी प्रमाण घेवूनही तणांचा उत्तम प्रकारे नायनाट होतो‼<br />\n&nbsp; &nbsp;यामुळे आपण मातीचे आरोग्य जपून तणनाशकांचा सुरक्षित वापर करु शकतो ‼<br />\n&nbsp; &nbsp;लक्षात ठेवा ...मातीचा वरचा 2 इंच थर कोरडा असल्याशिवाय तणनाशक वापरु नये‼<br />\n&nbsp; सोबत सॉइल चार्जर 10 ml किंवा फ्रूट चार्जर 20 ml प्रति लीटर घ्यावे ‼<br />\n<strong>&nbsp;राम सर&nbsp;<br />\nसेंद्रिय कर्ब व एकात्मिक शेती व्यवस्थापन मार्गदर्शक<br />\nSCT - वैदिक तंत्रज्ञान, नाशिक.<br />\nFCO टीम 8669200220/1/2/3/4/5/6/7/8</strong><br />\n​​</p>\n', '63_blog1.png', '63_blog2.png', 'Marathi', 0, 'article', 0, '2023-03-16 06:24:33', '2023-03-16 06:24:33'),
(64, 'SCT वैदीक चे प्रोडक्ट महाग का वाटतात ?', '<p><strong>&nbsp;आजचा प्रश्न&nbsp;<br />\nSCT वैदीक चे प्रोडक्ट महाग का वाटतात ?</strong><br />\n&nbsp; &nbsp;मित्रहो,&nbsp;<br />\n&nbsp; एक दोन प्रोडक्ट वापरले किंवा केमिकल सोबत मिक्स थेयरी वापरली तर असेच होणार , संपुर्ण टेक्नोलोजी वापरा , प्रोडक्ट म्हनून नाही‼️<br />\n&nbsp; &nbsp; हेच तर समजून घ्यायचे आहे , नेमकी चूकतेय कुठे ?<br />\n&nbsp; &nbsp; &nbsp;जर युट्यूबवर एक दोन नाही तर हजारो शेतकरी १००% वापर केल्यानंतर आणि पिक तयार झाल्यानंतर सांगतात की , टेक्नोलोजी स्वस्त आहे , आमचा खर्च ३०% - ४०% कमी झाला तर त्याचे काही तकी कारण असेलच ना ‼️<br />\n&nbsp; &nbsp; तुम्हाला महाग वाटते तर ते का महाग वाटते ?&nbsp;<br />\n&nbsp; &nbsp;तुमची शंका आहे , माहीती घ्या , यशस्वी सेतकर्यांसोबत बोला , फेल गेलेल्या शेतकर्यांना बोलाल तर तुम्ही फेल जाण्याचा मार्ग निवडाल , त्यात तुमचेच नुकसान आहे‼️<br />\n&nbsp; &nbsp; &nbsp;तुम्ही टेक्नॉलजी वापरून यशस्वी झालेल्या शेतकर्यांना विचारा , तेही सध्या वापरत असलेल्यांना विचारा‼️<br />\n&nbsp; &nbsp; जुन्या युजरना विचाराल तर त्यांचे आज वेगळे अनुभव असू शकतात कारण अनेकदा असा अनुभव येतो ओळखीची फायदा घेऊन आता आणून देतो म्हनून प्रोडक्ट घेतात व उधारी द्यायची म्हनून मग परत येतच नाहीत असे पण अनुभव येतात‼️<br />\n&nbsp; शेवटी माणूस आहे , दोन वर्षांपुर्वी तुमच्यासोबत असलेला मित्र आज तुमचा विरोधी असतो , त्यात काही विशेष नाही ‼️<br />\n&nbsp; &nbsp; &nbsp; तुम्ही गैरसमज केला म्हनून ज्यांना अनुभव आहे ते शेतकरी वापरायचे बंद नाही करनार परंतू तुम्हाला सत्य कळनार नाही‼️<br />\n&nbsp; &nbsp; &nbsp; &nbsp;सत्य हे आहे की ,&nbsp;<br />\n&nbsp; SCTवैदीक शेतकर्यां ना मार्गदर्शन करून समृद्धीकडे नेणारे तंत्रज्ञान आहे , आणि हे शेतकर्यांनीच मोठे केलेले आहे‼️<br />\n&nbsp; त्यामुळे कृषी अमृत ची किंमत &nbsp;८ वर्षांपासून ५५० रूपयेच आहे आमच्यासोबत असलेल्या सर्व ओरगॅनिक कंपन्यांनी १५०-२५० रूपये वाढवले.&nbsp;<br />\n&nbsp; &nbsp;महागाईचा फटका आपल्यालाही बसला पण आपन वितरकांचा व आपला नफा घटवून , शेतकर्यांवर पडनारा बोजा स्वतावर घेतला‼️<br />\nदुसरे उदा. पेस्ट फायटर संशोधन करून २८०० वरून ११५० वर कमी केले ‼️<br />\n&nbsp; &nbsp;तिसरे उदा. आपले वाटर चार्जर १०० मिली ३२० रू किमतीला आहे व मार्केटमध्ये त्याच्यासारखे वाटनारे २५० मिली ४५० ला आहे , किमतीवरून तुलना केली तर वाटर चार्जर महाग वाटते परंतू दोन्हींमध्यै १००० लीटरच औषध तयार होते म्हल्यावर वाटर चार्जर १३० रू स्वस्त आहे कारण प्रमाण ०.१ मिली प्रती लिटर आहे ‼️<br />\n&nbsp; &nbsp;चौथे उदा.<br />\n&nbsp; &nbsp;न्यूट्री व रूट चार्जरची ची किंमत ५०० रूपये आहे व ते ताकतीच्या बाबतीत कोणत्याही १००० रूपये किमतीपेक्षा जास्त खतांपेक्षा अधिक फायदेशीर व सुरक्षीत आहे‼️<br />\n&nbsp; &nbsp;वैदीक वापरले तर , पिकाचे आयुष्य व तोडे वाढतात , क्वालिटी व क्वांटीटी वाढते‼️<br />\n&nbsp; &nbsp;फवारणीतून वापरावयाचे वैदीकचे प्रोडक्ट दोनदा वापरता येतात तिथे खर्च निम्मा होतो ‼️<br />\n&nbsp; &nbsp;एक एकरमध्ये दोन एकरचे उत्पन्न निघते , ग्राहकांना पौष्टीक व वैदीक अन्न मिळते व शेतमालाची प्रतिष्ठा वाढते‼️<br />\n&nbsp; &nbsp; व्यापारी शोधत येतात , पुन्हा पुन्हा मागणी करतात‼️<br />\n&nbsp; &nbsp;सर्वांत महत्त्वाचे म्हनजे , पुढच्या पिकासाठी तसेच पुढच्या पिढीसाठी मातीचे आरोग्य सुधारत जाते ‼️<br />\n&nbsp; &nbsp;शेतकर्यांच्या कुटुंबाचे आरोग्य सुधारते ‼️<br />\n&nbsp; &nbsp; &nbsp;शेवटी तुम्ही जर २४ कॅरेट सोन्याची किंमत १ कॅरेट सोन्याचे पाणी दिलेल्या बेंटेक्स सोबत करत असाल तर आमचा नाविलाज आहे ‼️<br />\n&nbsp; &nbsp;हा झाला महाग या गैरमजाचा खुलासा‼️<br />\n&nbsp; &nbsp;दुसरा मुद्दा की हे सारखे वापरावे लागते ,&nbsp;<br />\n&nbsp; पहिली गोष्ट माती तील कस कोणी रिकामा केला ?<br />\n&nbsp; &nbsp; बागांचे शोषण करून त्या कमजोर कोणी केल्या ?<br />\n&nbsp; &nbsp;कुठे गेले डिकंपोजर , जिवामृत वापरनारे १०० पैकी १ शिल्लक आहेत ‼️<br />\n&nbsp; &nbsp; &nbsp;म्हनजे आधी आपन केमिकल व जैविक वापरून माती व बागांचा कस काढला आता तुम्हाला SCTवैदीक कडून मागची भरपाई पाहीजे व चालू पिकही चांगले आले पाहीजे , तुम्हाला हे अती होतेय असे वाटत नाही का ?<br />\n&nbsp; &nbsp; मागील काही वर्षांत मातीची व बागांची झालेली झीज भरून काढन्यासाठीच पुन्हा पुन्हा वापर करावा लागत आहे ‼️<br />\n&nbsp; &nbsp; बाबूराव हुले , नारोडी , आंबेगाव पुणे यांना भेटा , ते दोन वर्षांपासून १००% SCT व १० महिन्यांपासून १००% वैदीक वापरतात , ते आज सलग ३०-४५ दिवस एकही संरक्षण फवारणी घेत नाहीत तरी भाजीपाला , फळभाज्या एक नंबर उत्पन्न काढून मॅाल ला विकतात‼️<br />\n&nbsp; &nbsp;असे अनुभव शेकडों मित्रांना आलेले आहेत , तुम्ही पण घेऊ शकता‼️<br />\n&nbsp; &nbsp;दोडक्यासारख्या पिकात अतीशय कमी खर्चात एकरी ३० टन माल काढनारे शेतकरी आहेत‼️<br />\n&nbsp; &nbsp;वैदीकचे शेतकरी टोमॅटोत एकरी ५०० जाळ्या एका तोड्याला काढतात ‼️<br />\n&nbsp; &nbsp; ही यादी टाकली तर लेख खूप मोठा होईल , तुम्ही ते युट्यूबवर पाहून खात्री करू शकता ‼️<br />\n&nbsp; &nbsp;मित्रहो ,&nbsp;<br />\n&nbsp; आजपर्यंत तुम्हाला जे लुटत आले त्यांच्यामुळे तुमचा दृष्टीभेद झालेला आहे हे आम्ही समजू शकतो म्हनून आम्हीही तोच धंदा मांडलाय असा गैरसमज करून कृपया आमच्या &nbsp;, &ldquo; माती वाचवा , माती समृद्ध करा &ldquo; यासाठी सुरू केलेल्या SCTवैदीक अभियानाबद्दल शंका नको ‼️<br />\n&nbsp; &nbsp;ही कंपनी नसुन एक अभियान आहे ‼️<br />\n&nbsp; &nbsp; म्हणून तर ६५० व्हाट्सएप गृप व १५ युट्यूब चैनल वरून पारदर्शक मार्गदर्शनाचे काम चालते ‼️<br />\n&nbsp; &nbsp; &nbsp;भले तर वापरू नका , जोपर्यंत स्वताची खात्री होत नाही तोपर्यंत ‼️<br />\n&nbsp; परंतू अर्धवट माहीतीच्या आधारे मत तयार करू नका‼️<br />\n&nbsp; &nbsp;अन्यथा फेल गेलेल्यांच्या यादीत जाऊन बसाल‼️<br />\n&nbsp; &nbsp; SCTवैदीक ची ताकत एवढी जबरदस्त आहे की कितीही वातावरण बिघडू द्या रोग येण्याऐवजी पिकांवर , बागांवर आणखि तेज येते ‼️<br />\n&nbsp; &nbsp; अनुभव घ्याल तरच सत्य समजेल , दुसर्यांचे पाहून फार तर प्रेरणा घ्या , सत्य तुम्हाला स्वताला जाणून घ्यायचे आहे‼️<br />\n&nbsp; &nbsp;आमच्या यशस्वी युजर शेतकरी मित्र आणि वितरक , ओफीसर ( एफसीओ ) यांचे सोबत सल्लामसलत करूनच वापर करा ‼️रोज नियमित पणे युट्यूब व्हिडीओ पहा‼️<br />\n&nbsp; &nbsp;अपयशाची कोणतीच शक्य ता उरनार नाही , असाच आजपर्यंतचा अनुभव आहे ‼️<br />\n&nbsp; &nbsp; धन्यवाद&nbsp;<br />\n<strong>राम सर&nbsp;</strong></p>\n', '64_blog1.png', '64_blog2.png', 'Marathi', 0, 'article', 0, '2023-03-16 06:44:48', '2023-03-16 06:44:49'),
